const SEND_REQUEST = "SEND_REQUEST";

let initialState = {
  operators: [
    { id: 1, name: "MTC", photoURL: "#" },
    { id: 2, name: "Beeline", photoURL: "#" },
    { id: 3, name: "Megafone", photoURL: "#" }
  ],
  addNewOperator () {}
};

const Reducer = (state = initialState, action) => {
  switch (action.type) {
    case SEND_REQUEST: { 
      return  Math.random() >= 0.5;
    }
    default:
      return state;
  }
};
export default Reducer;
export const sendRequestCreator = () => ({ type: SEND_REQUEST });
