const SEND_REQUEST = "SEND_REQUEST";
let initialState = {
  operators: [
    {
      id: 1,
      name: "MTC",
      photoURL:
    `https://www.google.com/url?sa=i&url=https%3A%2F%2Ftechnovolga.ru%2Finfo%2Fbrands%2Fmegafon%2F&psig=AOvVaw0wnbAtQxm0AEyEeuOgjlye&ust=1585331844045000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCKDPvL3buOgCFQAAAAAdAAAAABAD`
    },
    { id: 2, name: "Beeline", photoURL: "../../public/bee.png" },
    { id: 3, name: "Megafone", photoURL: "../../public/mega.png" }
  ],
  addNewOperator() {}
};

const Reducer = (state = initialState, action) => {
  switch (action.type) {
    case SEND_REQUEST: {
      return Math.random() >= 0.5;
    }
    default:
      return state;
  }
};
export default Reducer;
export const sendRequestCreator = () => ({ type: SEND_REQUEST });
