const SEND_REQUEST = "SEND_REQUEST";
let initialState = {
  operators: [
    {
      id: 1,
      name: "MTC",
      photoURL: `https://technovolga.ru/upload/iblock/ecd/ecdfb7358b8c6d3c2842e6b786cc9c69.png`
    },
    { id: 2, name: "Beeline", photoURL: "../../public/bee.png" },
    { id: 3, name: "Megafone", photoURL: "../../public/mega.png" }
  ],
  addNewOperator() {}
};

const Reducer = (state = initialState, action) => {
  switch (action.type) {
    case SEND_REQUEST: {
      return Math.random() >= 0.5;
    }
    default:
      return state;
  }
};
export default Reducer;
export const sendRequestCreator = () => ({ type: SEND_REQUEST });
